mapper.insert:
insert
into 
	personal_project_reply
	(
		replynum
		, bnum
		, id
		, content
	)
values
	(
		personal_project_replynum_seq.nextval
		, #{bnum}
		, #{id}
		, #{content}
	)

insert
into
	personal_project_board
	(
		boardnum
		, id
		, title
		, content

		, filename
		, filesavename

	)
values 
	(
		personal_project_boardnum_seq.nextval
		, #{id}
		, #{title}
		, #{content}

		, #{filename}
		, #{filesavename}

	)

mapper.update:
update
	personal_project_board
set
	id= #{id}

	, title = #{title}


	, content = #{content}


	, filename = #{filename}
	, filesavename = #{filesavename}

where
	boardnum = #{boardnum}
